window.customNodeColors = window.customNodeColors || {};

const waveSurferCodeBase64 =
  "";

const decodedCode = atob(waveSurferCodeBase64);
const waveSurferBlob = new Blob([decodedCode], {
  type: "application/javascript",
});
const waveSurferBlobUrl = URL.createObjectURL(waveSurferBlob);

document.addEventListener("keydown", function (event) {
  if (event.ctrlKey && event.shiftKey && event.key.toLowerCase() === "f") {
    if (!document.getElementById("waveformvisualizerID")) {
      const iframe = document.createElement("iframe");
      iframe.id = "waveformvisualizerID";
      iframe.style.width = "500px";
      iframe.style.height = "300px";
      iframe.style.border = "2px solid #8A2BE2";
      iframe.style.borderRadius = "10px";
      iframe.style.position = "fixed";
      iframe.style.top = "50%";
      iframe.style.left = "50%";
      iframe.style.transform = "translate(-50%, -50%)";
      iframe.style.background = "#2D1B5A";
      iframe.style.zIndex = "1000";
      document.body.appendChild(iframe);

      const iframeHtml = `
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <title>Waveform Viewer</title>
    <style>
      body {
        background: #2d1b5a;
        color: #e0b0ff;
        font-family: Arial, sans-serif;
        margin: 0;
        padding: 20px;
        display: flex;
        flex-direction: column;
        align-items: center;
        height: 100vh;
        box-sizing: border-box;
      }

      #waveform {
        width: 90%;
        height: 200px;
        cursor: pointer;
        margin-top: 50px;
      }

      #controls {
        width: 90%;
        display: flex;
        justify-content: space-between;
        align-items: center;
      }

      #playPauseBtn {
        margin-top: -20px;
        padding: 6px 12px;
        font-size: 14px;
        background: #8a2be2;
        color: white;
        border: none;
        cursor: pointer;
        border-radius: 5px;
      }

      #timestampSlider {
        width: 300px;
        height: 6px;
        cursor: pointer;
      }

      .slider-wrapper {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
      }

      .slider-value {
        color: #e0b0ff;
        font-size: 14px;
        margin-top: 5px;
      }

      #closeBtn {
        position: absolute;
        top: 5px;
        right: 8px;
        background: #e74c3c;
        color: white;
        border: none;
        font-size: 14px;
        width: 24px;
        height: 24px;
        border-radius: 50%;
        cursor: pointer;
        text-align: center;
        line-height: 24px; /* vertically center the "X" */
        padding: 0; /* ensure padding doesn't mess up shape */
      }

      #loadingMsg {
        font-size: 16px;
        margin-top: 20px;
        animation: pulse 1.2s infinite;
      }

      @keyframes pulse {
        0% {
          opacity: 0.3;
        }
        50% {
          opacity: 1;
        }
        100% {
          opacity: 0.3;
        }
      }
    </style>
  </head>
  <body>
    <button id="closeBtn">X</button>
    <div id="loadingMsg">Loading waveform...</div>
    <div id="waveform"></div>
    <div id="controls">
      <button id="playPauseBtn">Play</button>
      <div class="slider-wrapper">
        <input type="range" id="timestampSlider" min="0" value="0" />
        <div id="timestampValue" class="slider-value">0:00</div>
      </div>
    </div>
    <script src="${waveSurferBlobUrl}"></script>
    <script>
      function formatTime(seconds) {
        const mins = Math.floor(seconds / 60);
        const secs = Math.floor(seconds % 60);
        return mins + ":" + secs.toString().padStart(2, "0");
      }

      let wavesurfer;
      let syncInterval;

      const playPauseBtn = document.getElementById("playPauseBtn");
      const timestampSlider = document.getElementById("timestampSlider");
      const timestampValue = document.getElementById("timestampValue");

      const startSync = () => {
        syncInterval = setInterval(() => {
          if (wavesurfer && wavesurfer.isPlaying()) {
            const currentTime = wavesurfer.getCurrentTime();
            const duration = wavesurfer.getDuration();
            if (!isNaN(currentTime) && !isNaN(duration)) {
              timestampSlider.value = currentTime;
              timestampValue.textContent = formatTime(currentTime);
            }
          }
        }, 200);
      };

      const stopSync = () => {
        clearInterval(syncInterval);
      };

      const initWaveSurfer = (audioUrl) => {
        try {
          document.getElementById("loadingMsg").style.display = "block";

          wavesurfer = WaveSurfer.create({
            container: "#waveform",
            waveColor: "rgb(200, 0, 200)",
            progressColor: "rgb(100, 0, 100)",
            url: audioUrl,
            backend: "MediaElement",
          });
        } catch (err) {
          console.error("[iframe/init] Failed to create WaveSurfer:", err);
          return;
        }

        wavesurfer.on("ready", () => {
          document.getElementById("loadingMsg").style.display = "none";

          const duration = wavesurfer.getDuration();
          timestampSlider.max = Math.floor(duration);
          timestampSlider.value = 0;
          timestampValue.textContent = formatTime(0);
        });

        wavesurfer.on("play", startSync);
        wavesurfer.on("pause", stopSync);
        wavesurfer.on("finish", stopSync);

        playPauseBtn.addEventListener("click", () => {
          if (!wavesurfer) return;
          wavesurfer.playPause();
          playPauseBtn.textContent = wavesurfer.isPlaying() ? "Pause" : "Play";
        });

        timestampSlider.addEventListener("input", (e) => {
          const seconds = parseFloat(e.target.value);
          wavesurfer.seekTo(seconds / wavesurfer.getDuration());
          timestampValue.textContent = formatTime(seconds);
        });

        wavesurfer.on("seek", () => {
          const currentTime = wavesurfer.getCurrentTime();
          timestampSlider.value = currentTime;
          timestampValue.textContent = formatTime(currentTime);
        });
      };

      window.addEventListener("message", (event) => {
        if (event.data.type === "updateAudio" && event.data.audioData) {
          if (wavesurfer) wavesurfer.destroy();
          initWaveSurfer(event.data.audioData);
        }
      });

      document
        .getElementById("closeBtn")
        .addEventListener("click", function () {
          let iframe = parent.document.getElementById("waveformvisualizerID");
          if (iframe) {
            parent.document.body.removeChild(iframe);
          }
        });
    </script>
  </body>
</html>

      `;

      const blob = new Blob([iframeHtml], { type: "text/html" });
      iframe.src = URL.createObjectURL(blob);

      iframe.onload = () => {
        const audioResource = runtimeScene
          .getGame()
          .getSoundManager()
          ._resourceLoader.getResource("audio.ogg");
        if (audioResource && audioResource.file) {
          const base64AudioData = audioResource.file;

          try {
            const byteCharacters = atob(base64AudioData.split(",")[1]);
            const byteArray = new Uint8Array(byteCharacters.length);
            for (let i = 0; i < byteCharacters.length; i++) {
              byteArray[i] = byteCharacters.charCodeAt(i);
            }

            const audioBlob = new Blob([byteArray], { type: "audio/mpeg" });
            const audioUrl = URL.createObjectURL(audioBlob);

            iframe.contentWindow.postMessage(
              {
                type: "updateAudio",
                audioData: audioUrl,
              },
              "*"
            );
          } catch (err) {
            console.error("[parent] Error decoding base64 audio data:", err);
          }
        } else {
          console.error("[parent] Audio resource or file is missing.");
        }
      };
    }
  }
});
